#!/usr/bin/env /usr/local/bin/node

// <bitbar.title>Jenkins Jobs</bitbar.title>
// <bitbar.version>v0.1</bitbar.version>
// <bitbar.author>burakkirkil</bitbar.author>
// <bitbar.desc>Lists all jobs</bitbar.desc>
// <bitbar.author.github>burakkirkil</bitbar.author.github>
// <bitbar.dependencies>node.js</bitbar.dependencies>

/******************************************************************************
 * OPTIONS
 *****************************************************************************/

const BLACK_WHITE_ICON = false
const SCHEMA = 'http' // http or https
const BASE_URL = ''
const JOBS = [
  'Important-Build-Job',
  'Super-Important-Deployer-Job'
]

const USER = ''
const TOKEN = '' // https://ci.company.com/user/{USERNAME}/configure`)

/******************************************************************************
 * WHERE THE MAGIC HAPPENS
 *****************************************************************************/
const IMAGE_BW_144dpi = ''
const IMAGE_COLOR_144dpi = ''

const HTTP_REQUESTER = require(SCHEMA)
const exec = require('child_process').exec
const refreshInterval = process.argv[1].split('.')[1]

const PRINT = []
const STATUS = {
  SUCCESS:  '✅',
  RUNNING:  '🔶',
  FAILURE:  '🚫',
  UNSTABLE: '⚠️',
  ABORTED:  '🔴',
  NOTBUILD: '⛔️'
}

const runningJobs = []
var failures = 0

function notify(options) {
  options.subtitle = options.subtitle || ''
  exec(`osascript -e 'display notification "${options.text}" with title "${options.title}" subtitle "${options.subtitle}" sound name "${options.sound}"'`)
}

function getMenu() {

  if (failures) {
    notify({
      title: `Jenkins`,
      text: `🔴 Error on ${BASE_URL}`
    })
  }

  if (runningJobs.length) {
    notify({
      title: `Jenkins`,
      subtitle: `${STATUS['RUNNING']} ${runningJobs.length} running on ${BASE_URL}`,
      text: runningJobs.join(`, `)
    })
  }

  const icon = BLACK_WHITE_ICON ? `templateImage=${IMAGE_BW_144dpi}` : `image=${IMAGE_COLOR_144dpi}`

  // Menubar Text & Icon
  console.log(`${runningJobs.length ? `(${runningJobs.length})` : ''}${failures ? '🚫' : ''} | ${icon} size=13`)
  console.log('---')

  // Submenu
  console.log(`Refresh Now... | refresh=true`)
  console.log(`Auto Refresh in ${refreshInterval}`)
  console.log('---')

  if (USER && SCHEMA && BASE_URL) {
    console.log(`${USER} on ${BASE_URL}`)
    console.log(`CI Home Page | href=${SCHEMA}://${BASE_URL}`)
    console.log(`${!TOKEN ? 'Get API Token ⚠️' : 'Settings'}`
      + ' | '
      + `href=${SCHEMA}://${BASE_URL}/user/${USER}/configure`)
  } else {
    var pluginsDirectory
    exec("echo $(defaults read com.matryer.BitBar pluginsDirectory)", function(err, stdout, stderr) {
      if (err) {
        console.log(err)
        pluginsDirectory = ''
      } else {
        pluginsDirectory = `${stdout}`
        console.log(
          'Enter Credentials ⚠️'
          + ' | '
          + `terminal=false bash=/usr/bin/open param1=${pluginsDirectory}`
        )
      }
    })
  }

  console.log('---')
}

function getUrl(jobName) {
  return `${SCHEMA}://${USER}:${TOKEN}@${BASE_URL}/job/${jobName}`
}

function handleJob(data) {
  if (data.result === null && data.building === true) {
    data.result = 'RUNNING'
    runningJobs.push(data.fullDisplayName)
  }

  data.result !== 'RUNNING' && data.result !== 'SUCCESS' && failures++

  if (data === Object(data)) {
    PRINT.push(`${STATUS[data.result]} ${data.fullDisplayName}`
      + ' | '
      + `href=${data.url}`
    )

    const date = new Date(data.timestamp)

    if (data.result !== 'SUCCESS' ) {
      PRINT.push(`-- 💻 Console Output | href=${data.url}console`)
      PRINT.push(`-- ---`)
    }

    PRINT.push(`-- ⏱ ${date.toLocaleString()}`)
    PRINT.push(`-- ---`)
    PRINT.push(`-- 🕓 Changes | href=${getUrl(data.name)}/changes`)
    PRINT.push(`-- 📁 Workspace | href=${getUrl(data.name)}/ws`)
    PRINT.push(`-- ▶️ Build | href=${getUrl(data.name)}/build?delay=0sec`)
    PRINT.push(`-- ⚙ Configure | href=${getUrl(data.name)}/configure`)
    PRINT.push(`-- ▶️ Rebuild Last | href=${getUrl(data.name)}/lastCompleteBuild/rebuild`)

  } else {
    PRINT.push(data)
  }
}

if (USER && TOKEN && SCHEMA && BASE_URL) {
  if (JOBS.length > 0) {
    const jobPromises = JOBS.map(jobName => new Promise((resolve, reject) => {

      const request = HTTP_REQUESTER.get(
        `${getUrl(jobName)}/lastBuild/api/json?pretty=true`,
        (response) => {
        var data = ''

        response.on('data', (chunk) => {
          data += chunk
        })

        response.on('end', () => {
          if (response.statusCode === 200) {
            const jobData = JSON.parse(data)
            jobData.name = jobName
            resolve(jobData)
          } else {
            failures++
            resolve(`‼️ There is no job named ${jobName} | color=red`)
          }
        })

        response.on('error', function(err) {
          failures++
          reject(err)
        })

      }).on('error', (err) => {
        failures++
        reject(err)
      })

      request.end()
    }))

    Promise
      .all(jobPromises)
      .then(jobs => {
        jobs.forEach(job => handleJob(job))
        getMenu()
        PRINT.forEach(line => console.log(line))
      })
      .catch(err => {
        console.log('---')
        console.log(`‼️ ${err} | color=red`)
      })
  } else {
    getMenu()
    console.log('⚠️ There is no job')
  }
} else {
  getMenu()
}
